#!/bin/bash

# Создаем простые одноцветные PNG иконки

# 16x16 фиолетовая иконка (base64)
echo "" | base64 -d > icon16.png

# 48x48 фиолетовая иконка с белым Q
echo "" | base64 -d > icon48.png

# 128x128 фиолетовая иконка с белым Q
echo "" | base64 -d > icon128.png

echo "Icons created successfully!"
ls -lh *.png
